SELECT s.LASTMODIFIEDDATE, s.FKORGANIZATIONNO, s.pksku as id, sc.IMAGE_STYLEID as styleId, s.skusize as "SIZE", s.fksizeno as sizeId, s.skudimension as dimension, s.skucolor as colorId, st.SIZE_EN, st.SIZE_FR FROM vstore.sku s INNER JOIN ELCAT.STYLE_COLOURS sc ON SUBSTR(sc.STYLEID,1,8) = s.fkstyleno AND sc.COLOUR_ID = s.skucolor INNER JOIN ELCAT.SIZE_TRANSLATION st ON st.SIZE_MERCH = s.SKUSIZE AND s.FKORGANIZATIONNO = 1 AND styleId IS NOT NULL WHERE  s.pksku = '-3195668' OR s.pksku = '-3195676'