const ca = [Buffer.from(params.mongoCertificateBase64, 'base64')];
const options = {
  ssl: true,
  sslValidate: true,
  sslCA: ca,
  useNewUrlParser: true
};

MongoClient.connect(params.mongoUri, options, function(err, client) {
  const collection = client.db(params.dbName).collection('bulkAtsRecalculateQueue');

  fs.readFile('./files/prod/styles_bulk_ats_PROD2.csv', 'utf-8', async (err, data) => {
    if (err) throw err;
    let dataRows = data.split('\n');
    console.log('starting record count', dataRows.length);
    const bulk = collection.initializeUnorderedBulkOp();
    await dataRows.forEach(async (row) => {
      //const record = await collection.findOne({ _id: row });
      //if(record) 
      const styleUpdate = {
        _id: row,
        insertTimestamp: (new Date()).getTime()
      };
      bulk.find({ _id: styleUpdate._id }).upsert().updateOne(styleUpdate);
    });
    console.log('insert count', dataRows.length);
    bulk.execute((err, result) => {
      console.log('finished', result);
      client.close();
      console.log('DB connection closed');
    });
    //bulk.execute();
  });
});
